import React from 'react'
import logo from '../assets/img/zila_parishad_rajshahi_logo.png'
import { Link } from 'react-router-dom'
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import { Autoplay, Pagination, EffectFade } from "swiper";

function Slider() {
  return (
    <>
      <div className="sliderPart">
        <div className="content">
          <Swiper
            autoplay={{
              delay: 3500,
              disableOnInteraction: false,
            }}
            pagination={{
              clickable: true,
            }}
            effect={"fade"}
            modules={[Autoplay, Pagination, EffectFade]}
            className="mySwiper bigswiper"
          >
            <SwiperSlide>
              <img src={e.img} alt="" />
              <div className="contect" data-aos="zoom-in-down">
                <h1>Bangladesh Polytechnic
                  <br /> Institute</h1>
                <p>{e.img}</p>
                <Link to="/courses">Courcses</Link>
              </div>
            </SwiperSlide>
          </Swiper>
        </div>
        <div className="logo_text">
          <Link to="/" className='row'>
            <img src={logo} alt="" />
            <div className="text">
              <h1>জেলা পরিষদ, রাজশাহী।</h1>
            </div>
          </Link>
        </div>
      </div>
    </>
  )
}

export default Slider
